sources.components/player/player.scss:
.player {
  padding: $spacing / 2;
  background-color: $clr-ui;
  box-sizing: border-box;
  height: 160px;
  &:nth-child(even) {
    background-color: $clr-ui-alt;
  }
  p {
    margin: $spacing / 5 0;
    color: $clr-txt-muted;
  }
  &-title {
    font-weight: 700;
    color: $clr-txt!important;
  }
  &-artist {

  }
  &-description {
    font-size: $fszxs;
  }
}
.custom-player {
  margin-top: $spacing / 2;
  display: flex;
  align-items: center;
  color: $clr-txt-muted;
  input[type="range"] {
    -webkit-appearance: none;
    width: calc(100% - 130px);
    margin: 0;
    padding: 7px 0;
    background: transparent;
    cursor: pointer;
    &::-webkit-slider-runnable-track {
      width: 100%;
      height: 2px;
      cursor: pointer;
      background-color: $clr-txt-muted;
    }
    &::-webkit-slider-thumb {
      -webkit-appearance: none;
      box-sizing: content-box;
      height: 16px;
      width: 16px;
      border-radius: 50%;
      background-color: $clr-txt;
      cursor: pointer;
      margin: -7px 0 0 0;
    }
  }
  .play-pause {
    width: 40px;
    height: 40px;
    margin-right: $spacing / 3;
    padding: 0;
    background-color: transparent;
    border: none;
    outline: none;
    cursor: pointer;
    background-image: url("./img/play_pause.svg");
    background-position: -4px center;
    background-repeat:no-repeat;
    background-size: cover;
    &.play {
      background-position: right center;
    }
  }
  .duration {
    margin-left: $spacing / 3;
  }
  .current-time {
    margin-right: $spacing / 3;
  }
  .duration, .current-time {
    font-size: $fszxs;
  }
}

sources.components/header/header.scss:
.main-header {
  background-color: $clr-ui-alt;
  @include mobile {
    background-color: transparent;
  }
  &.active {
    .main-nav {
      transform: translateY(0%);
    }
    .logo-mob {
      transform: rotate(180deg);
    }
  }
  .wrapper {
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    @include mobile {
      justify-content: flex-end;
      padding: 0 0 75px 0;
    }
  }
  .logo {
    img {
      display: block;
      &.logo-mob {
        display: none;
        transition: all 300ms;
        width: 40px;
        height: 40px;
        background-color: lighten($clr-ui,15%);
        border-radius: 50%;
        position: fixed;
        top: $spacing /2;
        right: $spacing /2;
        z-index: 2;
      }
      @include mobile {
        &.logo {
          display: none;
        }
        &.logo-mob {
          display: block;
          padding: 10px;
        }
      }
    }
  }
  .main-nav {
    @include mobile {
      width: 100%;
      transform: translateY(-100%);
      position: fixed;
      z-index: 1;
      top: 0;
      padding-top: 75px;
      background-color: $clr-ui-alt;
      transition: transform 300ms;
    }
    ul {
      list-style: none;
      margin: $spacing / 3 0;
      padding: 0;
      @include mobile {
        margin: $spacing / 2 0;
      }
      li {
        display: inline-block;
        @include mobile {
          display: block;
        }
        a {
          text-decoration: none;
          color: $clr-txt-muted;
          text-transform: uppercase;
          margin-left: $spacing;
          font-weight: 700;
          letter-spacing: 1px;
          @include mobile {
            margin: 0;
            padding: $spacing /1.5;
            display: block;
            width: 100%;
            text-align: center;
            box-sizing: border-box;
            font-size: $fszl;
            -webkit-tap-highlight-color: lighten($clr-ui-alt,1%);
          }
            &.active {
              color: $clr-txt;
              @include mobile {
                background-color: lighten($clr-ui,15%);
              }
            }
          @include hover-supported {
            &:hover {
              color: $clr-txt;
            }
          }
        }
      }
    }
  }
}

sources.components/release/release.scss:
.release-link {
  color: $clr-txt-muted;
  text-decoration: none;
}
.release {
  position: relative;
  .artwork {
    margin-bottom: $spacing / 1.5;
  }
  p {
    margin: $spacing / 5 0;
    &:last-of-type {
      margin: 0;
    }
  }
  &-title {
    font-weight: 700;
    color: $clr-txt;
  }
  &:hover {
    &:before {
      content: '';
      width: 100%;
      height: 100%;
      display: block;
      position: absolute;
      top: 0;
      left: 0;
      background-color: $clr-ui-alt;
      outline: $spacing / 2 solid $clr-ui-alt;
      z-index: -1;
    }
  }
}

sources.components/artwork/artwork.scss:
$size: 226px; //main page size
.artwork {
  width: $size;
  height: $size;
  overflow: hidden;
  @include mobile {
    width: 100%;
    height: 100%;
  }
  @include tablet {
    width: 100%;
    height: 100%;
  }
  img {
    height: 100%;
    width: 100%;
    object-fit: contain;
    display: block;
  }
}

sources.components/stores/stores.scss:
.stores {
  background-color: $clr-txt;
  &-description {
    font-size: $fszxs;
    color: $clr-txt;
    margin: $spacing / 2 0;
    text-align: center;
  }
  ul {
    list-style: none;
    padding: 0;
    margin: 0;
    li {
      display: flex;
      border-bottom: 1px solid lighten($clr-txt-muted,25%);
      &:last-of-type {
        border-bottom: none;
      }
      a {
        display: flex;
        justify-content: flex-end;
        align-items: center;
        width: 100%;
        padding: $spacing / 2;
        text-decoration: none;
        &:hover {
          background-color: lighten($clr-txt-muted,33%);
          button {
            background-color: $clr-ui-alt;
            color: $clr-txt;
            border-color: $clr-ui-alt;
          }
        }
        button {
          background-color: $clr-txt;
          color: $clr-txt-muted;
          border-width: 1px;
          border-style: solid;
          border-color: lighten($clr-txt-muted,25%);
          padding: $spacing / 3;
          text-transform: uppercase;
          width: 70px;
          cursor: pointer;
          font-size: $fszs;
        }
      }
    }
  }
  li {
    a {
      background-position: $spacing / 2 center;
      background-repeat: no-repeat;
      background-size: 130px;
    }
    &.beatport {
      a {
        background-image: url("./img/beatport_logo.svg");
      }
    }
    &.junodownload {
      a {
        background-image: url("./img/junodownload_logo.svg");
      }
    }
    &.spotify {
      a {
        background-image: url("./img/spotify_logo.svg");
      }
    }
    &.applemusic {
      a {
        background-image: url("./img/applemusic_logo.svg");
      }
    }
    &.traxsource {
      a {
        background-image: url("./img/traxsource_logo.svg");
      }
    }
    &.soundcloud {
      a {
        background-image: url("./img/soundcloud_logo.svg");
      }
    }
    &.deezer {
      a {
        background-image: url("./img/deezer_logo.svg");
      }
    }
    &.itunes {
      a {
        background-image: url("./img/itunes_logo.svg");
      }
    }
    &.tidal {
      a {
        background-image: url("./img/tidal_logo.svg");
      }
    }
  }
}

sources.components/release_page/release_page.scs:
$size: 320px;
.release-page {
  display: flex;
  align-items: center;
  flex-direction: column;
  .container {
    position: relative;
  }
  .back-btn {
    position: fixed;
    width: 60px;
    height: 60px;
    border-radius: 50%;
    box-sizing: border-box;
    top: 50%;
    transform: translate(-80px, -50%);
    background: $clr-txt url("./img/back_arrow.svg")no-repeat center;
    background-size: 25px;
    &:hover {
      background-color: lighten($clr-txt-muted,33%);
      &::after {
        content: "To releases list";
        color: $clr-txt;
        padding: 4px;
        background-color: $clr-ui;
        position: absolute;
        bottom: -71px;
        font-size: $fszs;
      }
      &::before {
        content: "";
        width: 0;
        height: 0;
        border-style: solid;
        border-width: 0 6px 6px 6px;
        border-color: transparent transparent $clr-ui transparent;
        position: absolute;
        left: 50%;
        bottom: -15px;
        transform: translateX(-50%);
      }
    }
  }
  .background {
    width: 100vw;
    height: 100vh;
    background-repeat: no-repeat;
    background-position: center;
    position: fixed;
    top: 0;
    left: 0;
    background-size: cover;
    z-index: -1;
    filter: blur(24px);
    opacity: .8;
  }
  .artwork {
    width: $size;
    height: $size;
    @include mobile {
      width: 100%;
      height: 100%;
    }
  }
  .stores, .player {
    width: $size;
    @include mobile {
      width: 100%;
    }
  }
}
