sources.components/release_page/release_page.scs:
$size: 320px;
.release-page {
  display: flex;
  align-items: center;
  flex-direction: column;
  .players {
    &.disabled {
      position: relative;
      &:before {
        content: 'loading...';
        position: absolute;
        display: flex;
        justify-content: center;
        align-items: center;
        background-color: $clr-ui-alt;
        width: 100%;
        height: 100%;
        opacity: .8;
        top: 0;
        left: 0;
        color: $clr-txt
      }
    }
  }
  @include mobile {
    &-body {
      .main-header {
        .wrapper {
          padding: 0;
        }
      }
    }
  }
  .container {
    position: relative;
  }
  .back-btn {
    position: fixed;
    width: 60px;
    height: 60px;
    border-radius: 50%;
    box-sizing: border-box;
    top: 50%;
    transform: translate(-80px, -50%);
    background: $clr-txt url("./img/back_arrow.svg")no-repeat center;
    background-size: 25px;
    @include mobile {
      display: none;
    }
    &:hover {
      background-color: lighten($clr-txt-muted,33%);
      &::after {
        content: "To releases list";
        color: $clr-txt;
        padding: 4px;
        background-color: $clr-ui;
        position: absolute;
        bottom: -71px;
        font-size: $fszs;
      }
      &::before {
        content: "";
        width: 0;
        height: 0;
        border-style: solid;
        border-width: 0 6px 6px 6px;
        border-color: transparent transparent $clr-ui transparent;
        position: absolute;
        left: 50%;
        bottom: -15px;
        transform: translateX(-50%);
      }
    }
  }
  .background {
    width: 100vw;
    height: 100vh;
    background-repeat: no-repeat;
    background-position: center;
    position: fixed;
    top: 0;
    left: 0;
    background-size: cover;
    z-index: -1;
    filter: blur(24px);
    opacity: .8;
  }
  .artwork {
    width: $size;
    height: $size;
    @include mobile {
      width: 100%;
      height: 100%;
    }
  }
  .stores, .player {
    width: $size;
    @include mobile {
      width: 100%;
    }
  }
}
